export const postDataToSheet = (selectedVideo, reviewer) => {
    // const GSHEETS_API_KEY = process.env.REACT_APP_GOOGLESHEETS_API_KEY
    const GSHEETS_SHEET_ID = process.env.REACT_APP_QA_TEST_SHEET_1_ID
    const ACCESS_TOKEN = process.env.REACT_APP_ACCESS_TOKEN

    const rowId =  selectedVideo.row 

    // const firstRow = 
    // const endRow = 

    return (dispatch) => {
        fetch(`https://sheets.googleapis.com/v4/spreadsheets/${GSHEETS_SHEET_ID}:batchUpdate`,{
            method: 'POST',
            headers:{
                "Content-Type": 'application/json',
                Authorization: `Bearer ${ACCESS_TOKEN}`
            },
                body: JSON.stringify({
                    requests:[{
                        repeatCell:{
                            range:{
                               startColumnIndex: 15,
                               endColumnIndex: 16,
                               startRowIndex: rowId,
                               endRowIndex: rowId,
                               sheetId: 0 
                            },
                            cell:{
                                userEnteredValue: {
                                    "stringValue": `${reviewer}`
                                },
                            },
                            fields: '*'
                        }
                    }]
                })
            }).then(response => response.json())
            .then(rxData => {
            console.log(rxData)
            if(rxData.error){
                alert(rxData.error.message)
            }else{

                console.log(rxData)
                dispatch({type: 'APPENDED_SHEET_DATA', sheetData: rxData})
            }
        })
    }

}
