import React from 'react'
import './qa_project.css'
import QaProjectInfo from './Qa_Project_Info'
import { connect } from 'react-redux'
import { fetchTrackingSheetData } from '../actions/fetchProjectSheetsData'
import { fetchReviewerSheetData } from '../actions/fetchProjectSheetsData'
import { updateProjectNameAndIds } from '../actions/updateProjectNameAndIds'
import { fetchProjects } from '../actions/fetchProjects'

class QaProjectOverview extends React.Component{

    constructor(){
        super()
        this.state = {
            project_name: "SyberSounds-Phase-II"
        }
    }

    componentDidMount(){
        debugger
        const nameFromPath = this.props.history.location.pathname.split("/").slice(-1)[0]
        const selectedProject = this.props.sheetData.projectsMaster.projects.find(proj => proj.name === nameFromPath) 
        debugger
        // if(this.props.sheetData.projectsMaster.projects.length > 0 ){
        //     debugger
        //     this.setState({
        //         project_name: selectedProject.name
        //     })
        //     this.props.fetchTrackingSheetData(selectedProject.trackingSheetId)
        //     this.props.fetchReviewerSheetData(selectedProject.reviewerSheetId)
        // }
        this.props.fetchTrackingSheetData(selectedProject.trackingSheetId)
        this.props.fetchReviewerSheetData(selectedProject.reviewerSheetId)
    }

    render(){

        return(
            <div className='project-div'>
                <div className="project-header">
                    <h1>{this.state.project_name}</h1>
                </div>
                {this.props.sheetData.qaProject.loading ? <h1>Loading....</h1> : <QaProjectInfo details={this.props.sheetData.qaProject}/>}
            </div>
        )
    }

}

const mapStateToProps = state => {
    debugger
    return {
        sheetData: state    
    }
}

const mapDispatchToProps = dispatch => ({
    fetchTrackingSheetData: () => dispatch(fetchTrackingSheetData()),
    fetchReviewerSheetData: () => dispatch(fetchReviewerSheetData()),
    // fetchProjects: () => dispatch(fetchProjects())
    // updateProjectNameAndIds: data => dispatch(updateProjectNameAndIds(data))


})

export default connect (mapStateToProps, mapDispatchToProps)(QaProjectOverview)
