import React from 'react'
import { useHistory } from 'react-router-dom'

const ProjectCard = (props) =>{

    const myDivToFocus = React.createRef()

    const history = useHistory();

    const clickHandler = () =>{
        // console.log(myDivToFocus)
        // dispatch an action to transfer project data to Redux for ProjectOverview
        history.push(`/qa-projects/${props.projectName}`)
        if(myDivToFocus.current){
            myDivToFocus.current.scrollIntoView({ 
               behavior: "smooth", 
               block: "nearest"
            })
        }

    }


    return(
        <div className="project-box" onClick={clickHandler}>
            <span className="proj-title">{props.projectName}</span>
        </div>
    )
}

export default ProjectCard
