import React from 'react'
import './qa_project.css'
import QaProjectInfo from './Qa_Project_Info'
import { connect } from 'react-redux'
import { fetchTrackingSheetData } from '../../actions/fetchProjectSheetsData'
import { fetchReviewerSheetData } from '../../actions/fetchProjectSheetsData'
import { updateProjectNameAndIds } from '../../actions/updateProjectNameAndIds'
import { fetchProjects } from '../../actions/fetchProjects'

class QaProjectOverview extends React.Component{

    constructor(){
        super()
        this.state = {
            selectedProject: {
                name:"",
                trackingSheetId: "",
                reviewerSheetId: ""
            }
        }
    }

    componentDidMount(){
        debugger
        if(!!this.props.sheetData.qaProject.trackingSheetId && !!this.props.sheetData.qaProject.reviewerSheetId){
            this.setState({
                name: this.props.sheetData.qaProject.name,
                trackingSheetId: this.props.sheetData.qaProject.trackingSheetId,
                reviewerSheetId: this.props.sheetData.qaProject.reviewerSheetId
            })
            this.fetchProjSheetsData()

        }else{
             this.getProjectInfo()
        }  

        // debugger
        // !!this.props.sheetData.qaProject.trackingSheetId && !!this.props.sheetData.qaProject.reviewerSheetId ? this.fetchProjSheetsData(this.props.sheetData.qaProject.trackingSheetId, this.props.sheetData.qaProject.reviewerSheetId ) : this.props.fetchProjects()
    }

    getProjectInfo = () => {
        this.props.fetchProjects()
        this.setState({
            name: this.props.sheetData.qaProject.name,
            trackingSheetId: this.props.sheetData.qaProject.trackingSheetId,
            reviewerSheetId: this.props.sheetData.qaProject.reviewerSheetId
        })
    }




    fetchProjSheetsData = () => {
        this.props.fetchTrackingSheetData(this.props.sheetData.qaProject.trackingSheetId)
        this.props.fetchReviewerSheetData(this.state.reviewerSheetId)
    }



    render(){

        return(
            <div className='project-div'>
                <div className="project-header">
                    <h1>{this.state.name}</h1>
                </div>
                {this.props.sheetData.qaProject.loading ? <h1>Loading....</h1> : <QaProjectInfo details={this.props.sheetData.qaProject}/>}
            </div>
        )
    }

}

const mapStateToProps = state => {
    debugger
    return {
        sheetData: state    
    }
}

const mapDispatchToProps = dispatch => ({

    fetchTrackingSheetData: (trackingSheetId) => dispatch(fetchTrackingSheetData(trackingSheetId)),
    fetchReviewerSheetData: (reviewerSheetId) => dispatch(fetchReviewerSheetData(reviewerSheetId)),
    fetchProjects: () => dispatch(fetchProjects())
    // updateProjectNameAndIds: data => dispatch(updateProjectNameAndIds(data))


})

export default connect (mapStateToProps, mapDispatchToProps)(QaProjectOverview)
