import { postErrorToTrackingSheet } from './postDataToTrackingSheet'
// THIS WHOLE FINDDRIVEFOLDERIDS FUNCTION SHOULD BE REFACTORED TO A FETCH FOLDER DATA FUNCTION

// const foundVideos = []
// const notFoundVideos = []

export const findDriveFolderIds = (selectionData) => {
    const ACCESS_TOKEN = process.env.REACT_APP_ACCESS_TOKEN
    // const DRIVE_ID = process.env.REACT_APP_GOOGLEDRIVE_PARENT_ID
    // const DRIVE_API_KEY = process.env.REACT_APP_GOOGLEDRIVE_API_KEY
    const selectedFileNames = []
    const selectedNamesAndIds = {}

    let fileName = `KS_${selectionData.video.mcVodId}_HD.wmv`

    return (dispatch) => {
        fetch(`https://www.googleapis.com/drive/v3/files?corpora=allDrives&includeTeamDriveItems=true&pageSize=1000&supportsAllDrives=true`, 
        { 
            headers: {
                authorization: `Bearer ${ACCESS_TOKEN}`
            }
        }).then(response => response.json())
        .then(rxData => {
            const reviewerFolder = rxData.files.find(file => file.name === selectionData.reviewer + "_QA") ? rxData.files.find(file => file.name === selectionData.reviewer + "_QA") : alert(`Can Not Find Folder For ${selectionData.reviewer}`)
            const oldParentFolder = "1shZeeuZyfUucN8egUBkemkmbexmkjWji"
            // GET FILE IDS OF SELECTED VIDEOS
            debugger
                if(rxData.files.find(file => file.name === fileName)){
                    const selectedDriveDataId = rxData.files.find(file => file.name === fileName).id  
                        const dataForUpdate = {
                            fileId: selectedDriveDataId,
                            fileName: fileName,
                            reviewerName: selectionData.reviewer,
                            reviewerFolder: reviewerFolder,
                            parentFolder: oldParentFolder
                        }
                        updateDriveFolders(dataForUpdate)

                }else{
                    // const missingProxy = selectionData.selectedVideos.find(video => `KS_${video.mcVodId}_HD.wmv` === fileName)
                    checkNextPage(rxData.nextPageToken, selectionData, reviewerFolder, oldParentFolder)
                } 
        })
    }
}

export const updateDriveFolders = (dataForUpdate) => {
    // const movedVideos = []
    const ACCESS_TOKEN = process.env.REACT_APP_ACCESS_TOKEN
    const url = `https://www.googleapis.com/drive/v3/files/${dataForUpdate.fileId}?addParents=${dataForUpdate.reviewerFolder.id}&removeParents=${dataForUpdate.parentFolder}`
    debugger
    fetch(url, {
        method: 'PATCH',
        headers: {
            authorization: `Bearer ${ACCESS_TOKEN}`
        }
    }).then(response => response.json())
    .then(rxData => {
        rxData.error ? alert(rxData.error) :alert(`You have moved ${dataForUpdate.fileName}`)
    })

}

export const checkNextPage = (nextPageToken, missingProxy, reviewerFolder, oldParentFolder) => {
    const ACCESS_TOKEN = process.env.REACT_APP_ACCESS_TOKEN
    const name = `KS_${missingProxy.video.mcVodId}_HD.wmv`
    debugger
    fetch(`https://www.googleapis.com/drive/v3/files?corpora=allDrives&includeTeamDriveItems=true&pageSize=1000&pageToken=${nextPageToken}&supportsAllDrives=true`, {
        headers:{
            authorization: `Bearer ${ACCESS_TOKEN}`
        }
    }).then(reponse => reponse.json())
    .then(rxData => {
        if(rxData.error){
            alert(`Can Not Find ${name}`)
            // postErrorToTrackingSheet(missingProxy)
        }else if(rxData.files.find(file => file.name === name)){
            const selectedDriveDataId = rxData.files.find(file => file.name === name).id

            const dataForUpdate = {
                fileId: selectedDriveDataId,
                fileName: name,
                reviewerFolder: reviewerFolder,
                parentFolder: oldParentFolder
            }
            updateDriveFolders(dataForUpdate)

        }else{
            checkNextPage(rxData.nextPageToken, missingProxy, reviewerFolder, oldParentFolder)
        }
    })

}
