// THIS WHOLE FINDDRIVEFOLDERIDS FUNCTION SHOULD BE REFACTORED TO A FETCH FOLDER DATA FUNCTION
export const findDriveFolderIds = (selectionData) =>{

// I WANT TO MOVE THE VIDEOS IN selectionData.selectedVideos TO selectionData.reveiewer 's DRIVE FOLDER
    const ACCESS_TOKEN = process.env.REACT_APP_ACCESS_TOKEN
    const DRIVE_ID = process.env.REACT_APP_GOOGLEDRIVE_PARENT_ID
    const DRIVE_API_KEY = process.env.REACT_APP_GOOGLEDRIVE_API_KEY
    const selectedFileNames = []
    const selectedNamesAndIds = {}


    selectionData.selectedVideos.forEach(video => {
        let name = `KS_${video.mcVodId}_HD.wmv`
        selectedFileNames.push(name)
    })

    return (dispatch) => {
        debugger
        fetch(`https://www.googleapis.com/drive/v3/files`, 
        {
            headers: {
                authorization: `Bearer ${ACCESS_TOKEN}`
            }
        }).then(response => response.json())
        .then(rxData => {
            debugger
            console.log(rxData)
            const reviewerFolder = rxData.files.find(file => file.name === selectionData.reviewer + "_QA")
            const oldParentFolder = rxData.files.find(file => file.name === "TO_BE_ASSIGNED")
            // GET FILE IDS OF SELECTED VIDEOS
            selectedFileNames.forEach(name => {
                const selectedDriveDataId = rxData.files.find(file => file.name === name).id
                selectedNamesAndIds[name] = selectedDriveDataId
            })

            for (let name in selectedNamesAndIds){
                let id = selectedNamesAndIds[name]
                const dataForUpdate = {
                    fileId: id,
                    fileName: name,
                    reviewerName: selectionData.reviewer,
                    reviewerFolder: reviewerFolder,
                    parentFolder: oldParentFolder
                }
                debugger
                updateDriveFolders(dataForUpdate)
            }
            rxData.error ? alert(rxData.error) : alert("Updating Folder Data...")
            // console.log("drive", rxData)

        })
    }
}

export const updateDriveFolders = (dataForUpdate) => {
    const ACCESS_TOKEN = process.env.REACT_APP_ACCESS_TOKEN
    const url = `https://www.googleapis.com/drive/v3/files/${dataForUpdate.fileId}?addParents=${dataForUpdate.reviewerFolder.id}&removeParents=${dataForUpdate.parentFolder.id}`
    fetch(url, {
        method: 'PATCH',
        headers: {
            authorization: `Bearer ${ACCESS_TOKEN}`
        }
    }).then(response => response.json())
    .then(rxData => {
        rxData.error ? alert(rxData.error) : alert('Google Drive has moved', rxData.name, "to", dataForUpdate.reviewerName)
    })
}
