import React from 'react'
import './qa_project.css'
import QaProjectInfo from './Qa_Project_Info'
import { connect } from 'react-redux'
import { fetchTrackingSheetData } from '../actions/fetchProjectSheetsData'
import { fetchReviewerSheetData } from '../actions/fetchProjectSheetsData'
import { updateProjectNameAndIds } from '../actions/updateProjectNameAndIds'

class QaProjectOverview extends React.Component{

    constructor(){
        super()
        this.state = {
            project_name: ""
        }
    }

    componentDidMount(){
        debugger
        const nameFromPath = this.props.history.location.pathname.split("/").slice(-1)[0]
        const selectedProject = this.props.projectsMaster.projects.length > 0 ? this.props.projectsMaster.projects.find(proj => proj.name === nameFromPath) : null
        this.setState({
            project_name: selectedProject.name
        })
        this.props.fetchTrackingSheetData()
        this.props.fetchReviewerSheetData()
    }

    render(){

        return(
            <div className='project-div'>
                <div className="project-header">
                    <h1>{this.state.project_name}</h1>
                </div>
                {this.props.sheetData.qaProject.loading ? <h1>Loading....</h1> : <QaProjectInfo details={this.props.sheetData.qaProject}/>}
            </div>
        )
    }

}

const mapStateToProps = state => {
    debugger
    return {
        sheetData: state    
    }
}

const mapDispatchToProps = dispatch => ({
    fetchTrackingSheetData: sheetId => dispatch(fetchTrackingSheetData(sheetId)),
    fetchReviewerSheetData: (sheetId) => dispatch(fetchReviewerSheetData(sheetId)),
    // updateProjectNameAndIds: data => dispatch(updateProjectNameAndIds(data))


})

export default connect (mapStateToProps, mapDispatchToProps)(QaProjectOverview)
